INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (1, '1', 'Torkel på värdshuset "Galna tunnan".', '1. Muckar gräl med storväxt person.', '2. Dricker öl.', '3. Träffar 1d6 äventyrare.', '4. Smiter från notan.', '5. Betalar notan och går ut på stan.', '6. Smiter in i rökigt sidorum.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (2, '2', '"Vad sjutton är det är?"', '1. Vapen.', '2. Juvel.', '3. Papper.', '4. Ring.', '5. Pinne.', '6. Kista.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (3, '3', 'Torkel hittar vapen.', '1. Ett svärd.', '2. Avbruten pil.', '3. Tvåhands stekpanna.', '4. Katapult.', '5. AK4.', '6. Flugsmälla.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (4, '4', 'Torkel hittar papper.', '1. Annons: "Äventyrare sökes".', '2. Reklam.', '3. Skattkarta.', '4. Pergamentrulle med trollformel.', '5. Kontrakt (skriver under...).', '6. Kontaktannons.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (5, '5', 'Torkel hittar svärd.', '1. Sword of dancing.', '2. Rostigt skräp.', '3. Hack i eggen.', '4. Som nytt.', '5. Magiskt svärd.', '6. Svärdet Alldräper.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (6, '6', 'Torkel träffar varelse av motsatt kön...', '1. Slåss med butig pojkvän.', '2. Blir nobbad.', '3. Torkel blir förälskad.', '4. Hoppsan! Flickan var visst en pojke.', '5. Trevlig skogspromenad.', '6. Flickan är inte så nogräknad.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (7, '7', 'Torkel i havet.', '1. Träffar Hajen XVII.', '2. Kan inte simma.', '3. Finner fast mark.', '4. Möter hungrig val. Ser inskription: "Jona was  Here". Valen finner dock Torkel osmaklig.', '5. Drar ut bottenpluggen.', '6. Möter en sjöjungfru.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (8, '8', 'Torkel på sta''n.', '1. Möter stadsvakt.', '2. "Nämen titta! vem är det där?"', '3. Tar en promenad.', '4. Hittar någonting som någon tydligen har        tappat.', '5. Lyckas hitta en affär.', '6. Torkel: Träffar blomsterförsäljeska vid namn   Eliza.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (9, '9', 'Torkel träffar äventyrare.', '1. "Lagom motstånd" tycker Torkel.', '2. De lejer Torkel som henchman för 2d6 gp.', '3. Torkel gillar dem, och tjatar till sig att få  följa med dem och äventyra.', '4. Torkel köper mystisk sak från deras senaste    äventyr av dem för 1d6 gp.', '5. Följer med på krogrond.', '6. Bland äventyrarna finns en houri. Torkel blir  totalt stunnad i tre rundor.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (10, '10', 'Torkel blir arg.', '1. Torkel slåss mot 1d6 medlemmar av              ordningsmakten.', '2. River ett hus.', '3. Torkel slaktar 1d6 oskyldiga bönder.', '4. Torkel håller andan tills han blir blå.', '5. Torkel går bärsärkagång.', '6. Torkel hittar någon som kan trösta honom.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (11, '11', 'Torkel försöker smita från notan.', '1. Slåss med värdshusvärden.', '2. Får diska av skulden och blir sedan brutalt    utslängd.', '3. Smiter ut bakvägen.', '4. Hittar inte ut.', '5. Hittar lönngång under bordet.', '6. Ser sig inte för; bildar hög med servitrisen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (12, '12', 'Torkel är kär.', '1. ...men inte flickan.', '2. Torkel förlovar sig.', '3. Torkel gifter sig.', '4. Går på månskenspromenad.', '5. Gör mystig utflykt.', '6. Flickan känner likadant som Torkel.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (13, '13', 'Torkel har otur...', '1. Snubblar över en inbillad sköldpadda och blir  mycket förvirrad. Stunnad i 3 ronder.', '2. DM blir arg på Torkel.', '3. Värsta utfallet som någonsin setts i en strid. Fienden stunnad i 3 ronder på grund av skrattan-  fall.', '4. Halkar på ett bananskal.', '5. Råkar begå självmord.', '6. Slår hål i luften. Kvävs.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (14, '14', 'Torkel med pinne.', '1. Hoppsan, det var visst en orm.', '2. En slagruta! Torkel provar den, och tror sig   genast vara en stor vattenåder på spåren. Han     går, och går, och går, och går...', '3. Guldplätterad champangnevisp.', '4. Pinne är märkt med konstiga runor.', '5. Stavhopparstav! Torkel tar sats... "Tjohooo!"  9 meter, nytt världsrekord.', '6. Glasspinne.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (15, '15', 'Torkel med kista.', '1. Kistan visar sig vara en mimic.', '2. Torkel hittar en sak i kistan.', '3. När Torkel efter mycket besvär fått upp        kistan, visar den sig vara tom.', '4. Kistan innehåller gammal fin konjak, tror      Torkel.', '5. Det var visst en likkista...', '6. Där som kistans botten borde ha varit gapar    ett svart hål. Torkel kryper ned.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (16, '16', 'Torkel med en ring.', '1. Rockring i skär plast.', '2. Upptäcker finstilt, ingraveradtext:            "önskering.   Gazonk Magi AB fråntar sig allt     ansvar för konse-    kvenserna av dåligt ordval,  stammning, eller använd- ande av slanguttryck     vid formulerandet av önskningar.US Pat. No 17-964  2-311."', '3. Guldring.', '4. Ringen har en inskription: "Asch nasch         gulasch.."', '5. Teleporteringsring.', '6. Könsbytarring.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (17, '17', 'Torkel teleporteras.', '1. ...till bergen.', '2. ...till kusten.', '3. ...till skogen.', '4. ...till en mörk plats.', '5. ...till öknen.', '6. ...till en öde ö.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (18, '18', 'Torkel i skogen.', '1. Torkel hittar en grotta.', '2. Torkel träffar någon.', '3. Torkel går vilse.', '4. Torkel fäller träd.', '5. Torkel orsakar skogsbrand.', '6. Torkel klättrar i träd.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (19, '19', 'Torkel träffar en person i skogen.', '1. Torkel är säker på att det är en rumpnisse.', '2. Det prasslar och knakar i buskarna.', '3. Torkel möter en grönklädd man med fjäder i     hatten. Denne presenterar sig som Robert          Locksley.', '4. En gråklädd man med stor hatt och långt skägg.', '5. En person med spetsiga öron.', '6. Ronja Rövardotter, som nu vuxit upp till en    stilig kvinna helt i Torkels smak.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (20, '20', 'Torkel träffar person med spetsiga öron.', '1. Svartalv.', '2. Mr Spock.', '3. Blek man med svart slängkappa.', '4. Fult litet kräk! Säkert en rumpnisse!', '5. Torkel tycker att personen ifråga ser på-      pälsad ut.', '6. Alvflicka.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (21, '21', 'Torkel vilse i skogen.', '1. Torkel tycker att mossan växer på fel sida om  träden.', '2. Torkel var ju här alldeles nyss!?', '3. Hittar en regnbåge.', '4. Upptagen som han är med att se sig för,        drattar Torkel ned i ett hål i marken.', '5. Torkel ser ett hus.', '6. Stannar upp ett tag för att se sig omkring.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (22, '22', 'Torkel står vid en grottingång.', '1. En vildsint grottbjörn dyker upp.', '2. Torkel går in. Mod i barm och vapen i arm.', '3. Torkel tycker att grottan har en konstig form. Den påminner honom om något...', '4. Det ligge rnågot och lurar där inne i dunklet.', '5. Dörren låst.', '6. Torkel träffar på en vacker neanderthalska.    Utövar tillämpad speleologi.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (23, '23', 'Torkel träffar på en alvflicka.', '1. ...men han ser bara henne som xp.', '2. Torkel tror hon är en ekorre.', '3. Flickan smiter (Flickan får 100 xp för gott    omdöme).', '4. Torkel säljer henne för 100 gp som ingrediens  till magiska drycker.', '5. Torkel tycker att hon är söt.', '6. Torkel förstår inte "nej" på alviska.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (24, '24', 'Torkel är ute med äventyrare.', '1. De möter någon.', '2. Uppsöker lokal grotta.', '3. Äventyrarna inser hurdan Torkel egentligen     är...', '4. De får ett Uppdrag.', '5. Övar in några nya svärdssvingar.', '6. En stunds avkopling vid lägerelden.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (25, '25', 'Torkel vid kusten.', '1. Sjörövare gör ett strandshugg för att gräva    ned en massa gp.', '2. Går och badar.', '3. Tycker klimatet är för fuktigt.', '4. Fångar fisk.', '5. Träffar någon på stranden.', '6. Torkel: Träffar en badflicka i minimalbikini.  Torkla: träffar butig badvakt i minimala          badbyxor.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (26, '26', '"Jaha, vad skall jag med den till?"', '1. Skänker den till en förbipasserande även-      tyrare.', '2. Äter den.', '3. Kastar bort den.', '4. Behåller den.', '5. Offrar den.', '6. Byter med förbipasserande äventyrerska.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (27, '27', 'Torkel ser ett flygande föremål.', '1. Det är en fågel.', '2. Det är ett flygplan.', '3. Nej, det är stålmannen!!', '4. Drake.', '5. Luden fladdermus.', '6. Ufo. 7. Du har fel tärning!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (28, '28', 'Torkel vid regnbågens fot.', '1. En man kommer traskande nedför bågen. Han      tror knappt sitt öga när han ser Torkel: "Hm!     Det måtte gå utför med Midgård..."', '2. Torkel hittar en kruka full med guld.', '3. Torkel blir blöt.', '4. Torkel möter en regnbågsdrake.', '5. Kliver upp på regnbågen.', '6. Möter Lucy, flickan med de kaleidoskopiska     ögonen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (29, '29', 'Torkel befinner sig i Dödskallegrottan.', '1. Får det onda märket.', '2. Får de goda märket.', '3. Smiter in bakom en björnfälla.', '4. Lyfter på masken. (Den som ser fantomens       ansikte dör en fasansfull död... gammalt          djungelordspråk)', '5. BLir biten av Devil.', '6. Stöter på Diana.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (30, '30', 'Torkel i affären.', '1. Torkel klunsar in i affären, varvid han slår   sönder dörren, välter en stor hylla med           kinesiskt porslin och skrämmer bort en massa      kunder. Affärsinnehavaren ingriper...', '2. Torkel köper knasigt spel.', '3. Torkel köper vapen.', '4. köper en bättre beagnad bl papegoja av norsk   härkomst.', '5. "Konstig affär. Det finns ju inga saker här    inne..."', '6. Köper gullig expedit.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (31, '31', 'Torkel möter Beholder.', '1. Beholdern tycker att Torkel är ful.', '2. Beholdern blänger på Torkel.', '3. Beholdern tittar tillgivet på Torkel.', '4. Beholdern ser på Torkel.', '5. Torkel ser på Beholdern.', '6. Torkel blir mirakulöst räddad.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (32, '32', 'Torkel i skum gränd.', '1. Blir anfallen av banditer.', '2. Sparkar omkull några soptunnor. Ur en av dem   trillar något...', '3. Ser inte avsaknaden av lock i gatan. Besöker   stadens VVSsystem.', '4. Smiter in genom en bakdörr.', '5. Får diskvatten i huvudet.', '6. Träffar en billig gatflicka.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (33, '33', 'Torkel dricker öl.', '1. Diskuterar liviligt med en rödbrusig person.', '2. Dricker mycket öl.', '3. Sjunger.', '4. Spiller under ölhäf.', '5. Vid ett tillfälligt besök under bordet ser     Torkel nå''t intressant.', '6. Mullig servitris.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (34, '34', 'Torkel i tempel.', '1. Diskuterar religion med präst.', '2. Torkel blotar.', '3. Torkel blottar sig.', '4. Torkel blottar dold dörr.', '5. Torkel finner blott en tofs.', '6. Diskuterar religion med prästinna.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (35, '35', 'Torkel träffar drake.', '1. Draken gillar grillad Torkel.', '2. Pappersdrake.', '3. Torkel upptäcker att draken har dålig ande-    dräkt.', '4. Lågflygande J35:a. Torkel känner ett häftigt   ryck i kragen och ett kraftigt luftdrag.', '5. Torkel och draken spelar tärning.', '6. Draken polymorferar sig till en eldig flicka.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (36, '36', 'Torkel blir nobbad.', '1. Får korgen.', '2. Super sig full.', '3. Finner sig i sitt öde.', '4. Tar det som en man.', '5. Envisas.', '6. Förstår inte vinken.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (37, '37', '', '', '', '', '', '', '........................');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (38, '38', 'Torkel läser scroll med magisk spell', '1. Där står HACKEM MUCHE.', '2. Där står XNATH DUAM.', '3. Där står HTOOTMIRG.', '4. Där står XAGYG YRAG.', '5. Där står ELIBIB YLOH.', '6. Där står FOOBIE BLETCH.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (39, '39', 'Torkel hittar skattkarta.', '1. Kan inte läsa.', '2. Förstår inte vad det är.', '3. Går på skattjakt i skogen.', '4. Går på skattjakt i bergen.', '5. Går på skattjakt i öknen.', '6. Ger den till vakcerk äventyrerska. Uttnyttjar  hennes tacksamhet.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (40, '40', 'Torkel sjunger.', '1. Nidvisa. Folk tar illa upp.', '2. Vidrigt falskt. BLir utslängd innan han vet    ordet av...', '3. "Jopp hej di..."', '4. "Nu skall vi ut och härja..." -och gör det!    Några äventyrare följer med.', '5. Hädisk visa. Den faller ej i god jord.', '6. Snuskig visa.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (41, '41', 'Torkel hädar.', '1. Pöbelhop vill lyncha Torkel.', '2. Gör bot och bättring.', '3. Kastas i fängelsehåla.', '4. Bränns på bål.', '5. Döms till halshuggning.', '6. Torkel blir förföljd av biilskna svärmar av    religiösa fanatiker. Då han stannar för att ta    itu med dem får de plötsligt något panikslaget i  blicken och flyr. "Fegisar!" muttrar Torkel och   vänder sig om för att gå vidare...');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (42, '42', 'Torkel vill ha det ultimata svaret.', '1. Ombeds vänta 7,5 miljoner år. Väntar tål-      modigt 1/525960 år.', '2. Får svaret, men förstår inte vad frågan var.   Blir frusterad.', '3. Funderar på vad 6*9 blir.', '4. Får svar på Magratheanska, men har ingen fisk  i örat. Förstår ingenting. Går och fiskar.', '5. Svimmar av lycka efter halva svaret.', '6. Får rätt svar. Förstår.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (43, '43', 'Torkel möter vis man.', '1. Får en sak av honom: "Detta skall hjälpa dig   i livets bistra strid och din kamp mot oför-      rätter och orättvisor."', '2. Frågar efter frågen till det ultimata svaret   på frågan om Livet, Universum och Allting.', '3. Blir ignorerad.', '4. Får en intressant flaska.', '5. Får lassvis med goda råd.', '6. Tröttnar på de djupa visdomsorden och letar    upp en ovis ung kvinna i stället.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (44, '44', 'Torkel träffar Gandlaf.', '1. Följer med till Moria. Slåss mot Balrog.', '2. Tycker Gandlaf har en fånig hatt.', '3. Får en ring av Gandalf.', '4. Går på hobfest.', '5. Ser på fyrverkeri.', '6. Torkel: Stöter ihop med Galadriel. Torkla:     Tycker att Gandalf har ett charmigt skägg.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (45, '45', 'Torkel i öknen.', '1. Möter beduiner.', '2. Torkel blir blåst av en sandstorm.', '3. Hittar karavanstig åt öster.', '4. Hittar karavanstig åt väster.', '5. Torkel hittar en pyramid.', '6. Slinker in i ett harem.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (46, '46', 'Torkel letar vatten.', '1. Träffar vattenelementar.', '2. Hittar bara sand. Forstätter att leta.', '3. Hittar olja.', '4. Hittar inget vatten.', '5. Hittar lite vatten.', '6. Hittar mycket vatten.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (47, '47', 'Torkel i harem.', '1. Träffar eunuck.', '2. Torkel vill stanna kvar. Haremets ägare        uppfyller hans önskan... och gör Torkel till      eunuck!', '3. FÖr mycket av det goda.', '4. Träffar mörkhårig haremsdam.', '5. Träffar kurvig arabiska.', '6. Träffar rådjursögd skönhet.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (48, '48', 'Torkel spelar spel.', '1. Diskuterar reglerna med motståndarna.', '2. Spelar "Drakar och Demoner."', '3. Spelar "Call of Cthulhu"; förlorar all SAN.', '4. Spelar kort.', '5. Spelar "Foolish Fantasy"', '6. Spelar Fia med knuff. Stöter på Fia.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (49, '49', 'Torkel fingrar på trollspö.', '1. ZAP!', '2. POP!', '3. WOOOSH!', '4. PHROOG!', '5. GLITSCHELL!', '6. SHREEK!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (50, '50', 'Torkel vid pyramid.', '1. Tar ett dopp i Nilen. Kolliderar med en        krockodil.', '2. Tar ett dopp i Nilen. Följer med strömmen.', '3. Går in i pyramiden.', '4. Gräver sig in i pyramiden.', '5. Trampar av näsan på sfinxen.', '6. Träffar Kleopatra.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (51, '51', 'Torkel dör hjältedöden.', '1. Kommer till Valhall.', '2. Blir återuppväckt vid stranden av en lång,     lång flod.', '3. Torkel är död länge. Blir animerad.', '4. Kommer till himmelen.', '5. Reinkarneras.', '6. Träffar dödsgudinnan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (52, '52', 'Torkel spelar kort.', '1. Torkel fuskar til sig 2d6 gp. 1d6 motspelare   kastar sig över honom. Torkel får visa att det    är ruter i honom.', '2. Avslöjad som falskspelare. Får lokal folk-     dräkt i tjära och fjädrar samt bärs ut på stock.', '3. Torkel får spader.', '4. Vinner storpott med en massa klöver och firar  med att gå krogrond.', '5. Torkel har bara två par i ess när mots-        tåndaren har kåk. Förlorar 2d6 gp.', '6. Spelar klädpoker med sin hjärter dam.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (53, '53', 'Torkel är snäll.', '1. Skänker bort alla pengar till välgörenhet.', '2. Ger bort 1d6 polkagrisar till små barn.', '3. Ger bort ben till 1d6 stackars utsvultna       hundar.', '4. Uttalar sig positivt om norrmän.', '5. Tar värning i frälsningsarmén.', '6. Hjälper dam över gatan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (54, '54', 'Torkel som skelett.', '1. Träffar 1d6 förstanivåsäventyrare.', '2. Turnas av 25:e nivåns cleric.', '3. Tappar huvudet, vilket används av känd dansk   prins.', '4. Träffar hungrig hund.', '5. Används på bilogilektion.', '6. Träffar vandöd av motsatt kön.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (55, '55', 'Torkel reinkarneras.', '1. ...som groda.', '2. ...som kobold.', '3. ...som alv.', '4. ...som människa.', '5. ...som riktigt svin (god jul!).', '6. "Tyvärr, inga liv på lager."');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (56, '56', 'Torkel som groda.', '1. Torkel träffar stork.', '2. Torkel äter 1d6 insekter.', '3. Ihjältrampad av förbipasserande äventyrare.', '4. Bjuder en gädda på middag.', '5. Träffar Plåt-Nicklas och Kalle Stropp.', '6. Blir kysst av prinsessa.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (57, '57', 'Torkel vid himmelens port.', '1. Träffar Sankte Per. Påstridig...', '2. Släpps inte in.', '3. Inser sin rätta plats.', '4. Smiter in.', '5. Torkel drar åt helvetet.', '6. Torkel lovar att gör bot och bättring.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (58, '58', 'Torkel i himlen.', '1. Dricker mineralvatten.', '2. Räknar får.', '3. Träffar Messiaskören. Sjunger med.', '4. Spelar harpa.', '5. Hittar hiss.', '6. Träffar Jungfru Maria.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (59, '59', 'Torkel i Vallhall.', '1. Slåss på Idavallen.', '2. Dricker mjöd.', '3. Äter av Särimner.', '4. Hittar en färggrann bro.', '5. Följer enögd man på vandring.', '6. Träffar flicka.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (60, '60', 'Torkel med valhallatös.', '1. Valkyria.', '2. Valkyria.', '3. Turistande Norna.', '4. Idun.', '5. Siv.', '6. Fröja.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (61, '61', 'Torkel träffar Idun.', '1. Äter äpple och föryngras 10 år.', '2. Äter för många äpplen.', '3. Bakar äppelpaj.', '4. Känner inte för äpplen idag.', '5. Jonglerar med äpplena.', '6. Struntar i äpplena.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (62, '62', 'Torkel får i uppdrag att...', '1. befria trakten från terroriserande monster.', '2. leverar ett viktigt meddelande.', '3. skaffa en sällsynt växt.', '4. driva in skatt.', '5. hitta en förlorad helig graal.', '6. befria en kidnappad prinsessa.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (63, '63', 'Torkel möter den dreglande dånfinken från Traal.', '1. Försöker övertyga den om att den inte är       hungrig.', '2. Kom ihåg att ta med sig sin handuk.', '3. Blir mumsigt mellanmål.', '4. Matar den med sin mormor.', '5. Citerar poesi av Gruntus den Väderspände.      Finken dånar av inre blödningar. Torkel drabbas   själv av skrumplever, gallsten, grå starr, och    bitmärken i benen.', '6. Blir mirakulöst räddad i sista stund.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (64, '64', 'Torkel i kloster.', '1. Far på korståg.', '2. Önksar att nunnorna inte hade egen avdelning.', '3. Vallfärdar till templet.', '4. Späker sig övernitiskt.', '5. Smiter,', '6. Slinker över till andra avdelningen och        fröjdar 1d6 nunnor.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (65, '65', 'Torkel på resebyrå.', '1. Reser norrut.', '2. Reser söderut.', '3. Reser österut.', '4. Reser västerut.', '5. Hittar kvarglömd koffert.', '6. T o R Bangkok.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (66, '66', 'Torkel förgriper sig.', '1. Strid mot 2d6 släktingar och goda vänner till  offret.', '2. Torkel misslyckas.', '3. Möter motstånd, envisas.', '4. Inser det löjliga i situationen.', '5. Torkel lyckas.', '6. Torkel får syn på bättre objekt att ägna sin   dyrkan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (67, '67', 'Torkel träffar varulv.', '1. Slåss.', '2. Blir biten.', '3. Ylar i kapp.', '4. Har varglav med sig.', '5. Diskuterar gemensamma erfarenheter.', '6. Djurisk passionerad varylva.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (68, '68', 'Torkel träffar vampyr.', '1. Torkel svingar svärdet.', '2. Åt vitlöksmacka till frukost.', '3. Torkel blir biten.', '4. Torkel blir utsugen.', '5. Vampyren förvandlar sig till en fladdermus     och flyr när den får syn på Torkel.', '6. Vampig flickvampyr.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (69, '69', 'Torkel älskar.', '1. Följer råden i "Så blir du en bättra älskare".', '2. Är slak.', '3. Oinspirerat.', '4. Åheej, å håå...', '5. "MERA! MERA!"', '6. Flickan salig, om igen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (70, '70', 'Torkel ute och reser.', '1. Möter någon.', '2. Kommer till de djupa skogarna.', '3. Kommer till öknen.', '4. Kommer till bergen.', '5. kommer till kusten.', '6. Kommer till bebodda trakter.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (71, '71', 'Torkel i skattgömma.', '1. Ser skylt: "Grimtooth was here".', '2. Finner ett föremål uppställt på en marmor-     piedestal.', '3. Träffar vaktande monster.', '4. Hittar juvel, sju uppsättningar ränslar,       hackor och spett , samt en väg ut.', '5. Hittar kista.', '6. Hittar skatt på 3d6*100gp.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (72, '72', 'Torkel möter lömskt vaktmonster.', '1. Ogre från ABAB.', '2. Golem med stenansikte.', '3. 1d6 lömska skelett.', '4. Representant för lokala ordningsmakten.', '5. Svavelosande demon.', '6. En fryntlig beholder.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (73, '73', 'Torkel avrättas.', '1. Flyktförsök: 2d6 vakter försöker stoppa        Torkel.', '2. Norsk bödel.', '3. Bödeln fullbokad de närmaste två månaderna.', '4. Bödeln missar.', '5. Bödlen träffar.', '6. Mirakulöst räddad i sista stunden.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (74, '74', 'Torkel har skojig ring.', '1. Uppsöks av 1d6 nazgûler.', '2. Osynlig - besöker dambastun - tappar ringen    - utslängd.', '3. Vill förstöra ringen själv.', '4. Lämnar ringen till SAKAB.', '5. Torkel blir nazgûl.', '6. Torkel gissar gåtor.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (75, '75', 'Torkel Glad.', '1. Bryter arm.', '2. Tar ett glas.', '3. Sjunger.', '4. Går ut och pinkar.', '5. Minnesförlust.', '6. Flörtar med fyllekompis.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (76, '76', 'Torkel möter kvinnlig vampyr.', '1. Butig pojkvän.', '2. Blir nobbad.', '3. Middag med blodig biff och rödvin.', '4. Visar korsryggen.', '5. Torkel flyr.', '6. Häftig kärlekstund.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (77, '77', 'Torkel får radies.', '1. Slåss mot 1d6 hundfångare.', '2. Ylar mot månen.', '3. Tuggar fradga.', '4. Blir botad av förbipasserande paladin. Torkel  slår följe med honom.', '5. Uppsöker veterinär.', '6. Sprider smittan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (78, '78', 'Torkel önskar.', '1. Önskar sig en till önskning.', '2. Begravd under 16 ton guld.', '3. Önskar sig ett svärd. "Poof!" säger det, och   Torkel känner sig plötsligt omlokaliserad.', '4. Önskar sig en tunna öl i glada vänners lag.', '5. Önskar god middag.', '6. Önskar trevlig helg.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (79, '79', 'Torkel spelar tärning med drake.', '1. Draken har spelat med Torkel förr.', '2. Torkel fuskar till sig en kul pryl.', '3. Bränner sina chanser.', '4. Torkel upptäcker att draken använder falska    tärningar.', '5. 16 tons tärning. Torkel studerar undersidan.', '6. Torkel smiter.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (80, '80', 'Torkel som vampyr.', '1. Slåss mot 1d6 äventyrare.', '2. Möter 42:a levels cleric med vitlöksandedräkt', '3. Stiger upp på morgonen, som han är van att     göra.', '4. Nattlig lunch på oförsiktig bonde.', '5. Uppsöker tandläkare; blir botad.', '6. Skaffar kvinnlig (blivande) vampyr från        närliggande värdshus.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (81, '81', 'Torkel i Bergen.', '1. Möter 1d6 norrmän.', '2. Blir bespottad av lama.', '3. Hittar lamakloster.', '4. Hittar tornkrökt byggnadsverk.', '5. Torkel klättrar.', '6. Möter Bergakungens dotter.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (82, '82', 'Torkel har en lampa.', '1. Torkel tänder lampan.', '2. Torkel saknar lampolja.', '3. Gnider lampan, ett enormt svart rökmoln        kommer fram. Torkel dör av kolmonoxidförgiftning', '4. Gnider lampan, en Djinn kommer fram i ett      gigantiskt moln av rök.', '5. Gnider lampan, en Djinn kommer fram i ett      gigantiskt moln av rök.', '6. Gnider lampan , en eterisk Djinnflicka kommer  fram i ett sirligt moln av parfyrmerad rök.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (83, '83', 'Torkel i dungeon-rum.', '1. Rummet verkar bebott.', '2. Rummet verkar vara en skattkammare! Torkel     hoppar in.', '3. Dörren slår igen bakom Torkel och inga andra   utgångar syns. Som tur är hittar Torkel en        hemlig dörr på en närliggande vägg.', '4. Lucka i taket.', '5. Lucka i golvet.', '6. "Ååh! Pengar & xp överallt & bara en liten     sovande ödla som vaktar dem."');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (84, '84', 'Torkel i dungeon.', '1. 1d6+1 meter bred standardiserad dungeon-gång.', '2. Detta verkar vara en typisk gammal gruva.', '3. Hackar ihjäl 1d6 högljudda svampar.', '4. Följer spår.', '5. Kommer in i ett enormt rymligt grottrum.', '6. Går ett tag innna han slår näsan i en dörr.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (85, '85', 'Torkel i dungeon-gång.', '1. Plockar upp 1d6 kringspridda gp.', '2. Kommer till en korsning.', '3. Gången slutar med en dörr.', '4. Dungeon-gången leder fram till en trappa.', '5. Något prasslar plötsligt till bakom Torkel.    Han är inte ensam här!', '6. Torkel finner något värt en nogrannare under-  sökning.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (86, '86', 'Torkel i katakomberna.', '1. Torkel slåss mot 2d6 skelett.', '2. En wraith lurar i skuggorna, och snor hälften  av Torkels xp.', '3. Kommer fram till en dörr.', '4. I ett mörkt hörn står en svartbetsad kista     med förgyllda beslag.', '5. Traskar en trappa upp. Slår nästan huvudet i   en avspärrad lucka.', '6. Snubblar över ett gångjärn. Det är en lucka i  golvet!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (87, '87', 'Torkel bryter upp dörr.', '1. Dörrens ägare blir arg på Torkel.', '2. Dörren visar sig leda in i ett skåp. Därinne   finner Torkel någonting.', '3. Kommer ut i gränd.', '4. Bakom dörren finns en trappa.', '5. Det bullrar rätt ordentligt när Torkel         tacklar dörren.', '6. Lyckas göra det ljudlöst.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (88, '88', 'Torkel har hittat nå''t.', '1. En flaska.', '2. En pinne.', '3. En pergamentrulle.', '4. En lampa.', '5. 1d6*100 gp och en rolig pryl.', '6. Hittar 1d6 flaskor vin.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (89, '89', 'Torkel öppnar lucka i golvet.', '1. Lång trappa nedåt.', '2. Något kortare trappa nedåt.', '3. Det finns en kista under luckan.', '4. Lönngång.', '5. Torkel fallar ned i avloppet.', '6. Luckan väger 16 ton, Torkel ger upp.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (90, '90', 'Torkel öppnar lucka i taket.', '1. Kommer upp i ett litet vitt tält med trätak,   under vars väggar en massa fötter sticker in.     Torkel lyfter något skamset på bordsduken och     kryper fram.', '2. Torkel kommer pp i en mörk stengång med en     massa konstiga bilder på väggarna.', '3. Lönnlucka. Torkel kommer ut i mitten på en     jättelik mosaikbild.', '4. En massa lös jord rasar in och Torkel får      grus i ögonen. En ekorre faller död ner           (hjärtattack) då den hör försskräckliga eder      komma ur ett hål i marken.', '5. Kommer upp genom golvet i att mörkt rum.       Luften känns unken och instängd.', '6. Över luckan ligger en djurhud. Torkel kikar    in i ett grottliknande rum med en konstig tron.   Beslutar sig för att smita upp trots allt.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (91, '91', 'Torkel äter.', '1. Det smakar gott; Torkel tar en tugga till.', '2. Torkel tycker att det dög som en närande       frukost.', '3. Hmmm... lite mer salt skulle inte skada...', '4. Torkel tycker det hela var smaklöst.', '5. Torkel får ont i magen.', '6. Spyr. Urrrk...');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (92, '92', 'Torkel ser en fågel.', '1. Quack-Fu-anka.', '2. Torkel tar fram sin trogna bågge, och snart    puttrar en väldoftande fågelgryta över den hem-   trevliga lägerelden.', '3. Torkel får guano i ansiktet.', '4. Det var en häg(e)ring.', '5. Torkel försöker följa fågeln.', '6. Fågeln, en svan, landar vid en lite sjö. Med   elegant rörelse kastar den av sig sin svanhamn    och blir en vacker flicka.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (93, '93', 'Torkel försöker flyga.', '1. Torkel är för dum för att inse att det inte    går - och lyckas!', '2. Torkel skuttar omkring och flaxar med armarna.', '3. Torkel inser hur dumt han beter sig.', '4. Torkel kastar sig mot marken och missar.', '5. Torkel kastar sig utför ett stup med djupt     vatten nedanför.', '6. Torkel kastar sig utför ett stup med vassa     klippor nedanför.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (94, '94', 'Torkel flyger.', '1. Närkontakt med ett berg.', '2. Närkontakt av den varma graden.', '3. Närkontakt av den tredja graden.', '4. Möter eskader med 1d6 drakar.', '5. Gör en stilfull kraschlandning i skogen.', '6. Beundrar utsikten.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (95, '95', 'Torkel hos Robin Hood.', '1. Torkel råkar möta sheriffen av Nottingham med  1d6 soldater.', '2. Torkel spelar fotboll i Nottingham Forest.', '3. Diskuterar gemensamma erfarenheter med broder  Tuck.', '4. Torkel slåss med en butig karl på en trästock  och blir blöt.', '5. Torkel blir bestulen på 1d6 gp. Beslutar sig   lämna sällskapet.', '6. Torkel stöter på Marion.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (96, '96', 'Torkel dricker rolig dryck.', '1. Magisk Coca-Cola.', '2. Hmmm... Smakar gott!', '3. Det fanns en ande i flaskan.', '4. Det gift som omger Visby stift.', '5. Torkel känner sig ovanligt lätt om hjärtat.', '6. Kärleksdryck.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (97, '97', 'Torkel i vulkan.', '1. Torkel irriterar sig på en salamander.', '2. Torkel finner en mörk, sval tunnel att pusta   ut i.', '3. Träffar person från Vulcan.', '4. Surfar ned på en lavaström.', '5. Grillar korv.', '6. Lyckas förorsaka ett rejält vulkanutbrott.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (98, '98', 'Torkel försöker öppna en dörr.', '1. Kommer in i ett rum.', '2. Låst!', '3. Torkel reagerar försent när dörren bakom       honom slår igen. Bakom honom har han nu bara en   stenvägg. Ingen återvändo!', '4. Ett stort naturligt grottrum ligger bakom      dörren.', '5. Slarvigt byggd teleportationsdörr!', '6. Hittar en kidnappad prinsessa bakom dörren.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (99, '99', 'Torkel bestiger berg.', '1. Möter stenjätte.', '2. Startar ett bergskred.', '3. Försvinner upp i molnen.', '4. Möter andra bergsbestigare.', '5. Ser röksignaler från toppen. Beslutar sig för  att undersöka vem som är ansvarig.', '6. Venusberg!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (100, '100', 'Torkel vill föstöra ringen.', '1. Slåss mot 1d6 nazgûler.', '2. Slåss mot 2d6 orcher.', '3. Slåss mot Shelob.', '4. Slåss mot Sauron.', '5. Ångrar sig.', '6. Lyckas förstöra ringen!!!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (101, '101', 'Torkel i fängelsehåla.', '1. Slåss mot 2d6 fångvaktare under flyktbesök.', '2. Torkel landsförvisad. BLir ivägskickad norrut.', '3. Täljer 1d6 trägubbar.', '4. Rymmer genom grev-grävd tunnel.', '5. Döms till döden.', '6. Kvinnlig fångvaktare låter sig tillfälligt     intagas av Torkels animaliska dragningskraft.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (102, '102', 'Torkel letar fällor.', '1. Ändrar sig, vänder och går åt andra hållet.', '2. Hittar ingen fälla.', '3. Hittar ingen fälla - tyvärr!', '4. Förvissar sig om att det inte finns någon      fälla här.', '5. Hittar en fälla och undviker den.', '6. Hittar en fälla och desarmerar den.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (103, '103', 'Torkel går i fälla.', '1. Hamnar i ormgrop med 1d6 ormar.', '2. Faller i 10 fot djup grop.', '3. En fallucka ned i en underjordisk flod.        Torkel spolas med strömmen.', '4. Ett 16 ton tungt stenklot närmar sig oro-      väckande fort.', '5. Intrasslat i nät, tillfångatagen av kobolder.', '6. Blir beramlad av piercer (skäms!!!)');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (104, '104', 'Torkel hittar trappa i dungeon.', '1. På trappkrönet finner Torkel en lemlästad      äventyrare. som Torkel genast plundrar.', '2. Trappan kommer upp genom en brunn i en vacker  trädgård.', '3. Trappan går upp till en lucka.', '4. "Hmmm... Ovanligt låga trappräcken... och      inte lutar trappan heller..." Torkel blir         översprungen av ett 150 meter långt, tutande,     grön drake med många runda fötter och ännu fler   lysande ögon.', '5. Torkel går uppåt en lång, lång tid, tills jan  kommer till en öppning och kan beskåda utsikten.', '6. Torkel går uppåt en lång, lång tid men vänder  och går upp igen då han hör ljudet av första      berida balrogbataljonens paradmarsch. På åter-    vägen kommer någon honom till mötes.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (105, '105', 'Torkel letar efter en borttappad helig graal.', '1. Träffar svart riddare.', '2. Inser att Camelot bara är en kuliss.', '3. Bygger in våldet i systemet genom att för-     trycka 1d6 klassmedvetna bönder i ett autonomt    syndikalistiskt kollektiv.', '4. Hittar två kokosnötshalvor och rider ut på     nya äventyr.', '5. Ser en älg.', '6. Stöter på trubbnäst häxa.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (106, '106', 'Torkel blir charmad av houri.', '1. Torkel var inte ensam om att bli charmad.1d6   andra äventyrare vill också ha ett finger med i   spelet.', '2. Torkel blir våldtagen.', '3. Hourin känner sig trygg i Torkels sällskap.    (Dumma flicka!)', '4. Torkel får en passionerad kyss.', '5. Hourin förälskar sig i Torkel och för bort     honom.', '6. Torkel förför hourin!!!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (107, '107', 'Torkel möter Stålmannen.', '1. Stålmannen gör sin plikt för att skydda all-   mänheten.', '2. Torkel får lift till en närbelägen galax.', '3. Torkel känner sig genomskinlig.', '4. Torkel inser att Stålmannen är starkare än     han - blir djup deprimerad.', '5. Torkels sköld var visst av kryptonit.', '6. Torkel stöter på Lois Lane.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (108, '108', 'Torkel med AK4.', '1. Gör vapenvård. Får en massa delar över.', '2. Plockar isär den och sätter iop den 2d6        gånger med bindel för ögonen.', '3. Prövar den i nästa strid.', '4. Tömmer magasinet i en folksamling, mejar ner   4d6 personer.', '5. Tittar i pipan och fingrar på avtryckaren.', '6. Sexfixerad som Torkel är, tror Torkel att det  är en massagestav. "Kul pryl!"');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (109, '109', 'Torkel vid bro.', '1. River bron och slåss med ett uppretat troll.', '2. Torkel hör en mullrig basröst: "Vem är det     som klampar på min bro?"', '3. Ropar "Hej"; Träbron rämnar under Torkel       enorma tyngd. Torkel hamnar i floden och spolas   nedströms på spillrorna.', '4. Hoppar över bäcken och ropar "Hej" sedan.', '5. Bygger en egen bro och går över den istället.', '6. Stoppas av en svartplåtad figur:               "None shall pass..."');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (110, '110', 'Torkel avreagerar sig.', '1. Mejar ner 2d6 soldater.', '2. Tuggar fradga och slår vilt omkring sig. Blir  omhändertagen av vänliga män i vita rockar och    får en trevlig kramtröja.', '3. Trampar ihjäl 3d6 marsvin.', '4. Slår huvudet i väggen.', '5. Vrider nacken av en katt.', '6. Hackar brudfölje med 2d6+6 personer och våld-  tar bruden.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (111, '111', 'Torkel flyr.', '1. Fienden hinner ikapp Torkel.', '2. Torkel gömmer sig i ett hus.', '3. Torkel gömmer huvudet i sanden. Undgår upp-    täckt.', '4. Torkel springer in i någon.', '5. Torkel springer länge.', '6. Torkel springer ikapp spänstig löpartjej.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (112, '112', 'Torkel tröttnar.', '1. Torkel drar åt skogen.', '2. Torkel drar åt helvetet.', '3. Torkel drar dit pepparn växer.', '4. Torkel drar så långt vägen räcker.', '5. Torkel går och badar.', '6. Går och går men kommer aldrig till dörren.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (113, '113', 'Torkel gömmer sig i byggnad.', '1. Paladinskolans huvudkontor.', '2. Trevligt hus där man delar ut "älska-dig-      själv"-tröjor.', '3. Slott.', '4. Affär.', '5. Värdshus.', '6. Hus med många flickor.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (114, '114', 'Drar i snöre.', '1. Högt över Torkels huvud hörs dånet av gigan-   tiska bronsklockor: "Ding-dång!" Den puckel-      ryggiga ringaren är mycket förgrymmad över att    Torkel har invaderat hans klockrike.', '2. Torkel hör ett gurglande, brusande ljud...', '3. En storväxt blek betjänt dyker upp: "You rang  sir..."', '4. Hittar en tand.', '5. Följer den röda tråden.', '6. Torkel drar och drar... Till slut står Torkel  där med ett garnnystan i ena handen och en        flicka utan en tråd på kroppen i den andra.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (115, '115', 'Torkel vill in genom en låst dörr.', '1. Dörrens ägare blir arg.', '2. Mosar dörren med ett rejält hugg.', '3. Rejäl stendörr.', '4. Slår sönder dörren och går in. Tycker sig      känna igen den välbekanta lukten av rumpnissar.', '5. Hittar nyckeln under dörrmattansom det står    "Välkommen" på.', '6. Knackar på. En hemtrevlig liten hobflicka      öppnar.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (116, '116', 'Torkel möter grottluffare.', '1. Inte rumpnissar nu igen!', '2. 1d6 orcher.', '3. Drake.', '4. 3d6 fladdermöss.', '5. Lich.', '6. Vilsegången äventyrerska.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (117, '117', 'Torkel följer ledtråd.', '1. Imse vimse...', '2. Kommer till ett konstigt rum.', '3. Verkar vara nära gåtans lösning.', '4. Hittar en rutig hatt, förstorningsglas, fiol   och en pipa.', '5. Rullar upp tråden i ett prydligt nystan,       medan han följer den genom vindlande gångar.      Plötsligt tar den slut.', '6. Träffar norna.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (118, '118', 'Torkel i dungeon-korsning.', '1. En av gångarna leder uppenbart in till ett     rum.', '2. Forsätter rakt fram.', '3. Svänger av åt höger, bara för att snubbla      över en lösaktig sten och falla rakt ner i en     djup brunn.', '4. Tar gången som går till vänster.', '5. Slår en tärning om vilken väg som är bäst.', '6. Torkel är trött på den här %#"¤#&%#!!!         grottan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (119, '119', 'Torkel och Oden.', '1. Slåss mot lindorm.', '2. Går på sightseeing i Valhall.', '3. Slåss mot 1d6 jättar.', '4. Hittar en 10 meters vante.', '5. Torkel blåser i horn. Utlöser Ragnarök!', '6. Torkel stöter ihop med Frigg.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (120, '120', 'Torkel möter den svart riddaren.', '1. Den svart riddaren får in en lyckoträff.', '2. Torkel lemlästar den svarta riddaren. Den      svarte riddaren tycker det är ovagjort.', '3. Torkel avlägsnar armar och ben från sin mot-   ståndare. Torkel tror sig ha vunnit, när den      svarte riddaren blöder på Torkel.', '4. En av den svarte riddarens lemmar slits loss   av en välriktad träff. Blodet sprutar men         riddaren säger bara: "Det är bara en skråma!"', '5. Hugger av riddarens svärdsarm. tar hans svärd  och sticker.', '6. Torkel pustar ut ett tag med ivrig heja-       klackslederska medan riddaren tar hand om 2d6     äventyrare.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (121, '121', 'Torkel räddar trakten.', '1. "Är det där kobolder? Jag som trodde det var   rumpnissar!"', '2. 3d6 orcher', '3. Sibirisk jättejakttussilago.', '4. Tyrannosaurus Rex.', '5. Drake.', '6. Häxa.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (122, '122', 'Torkel som budbärare.', '1. BLir överfallen av postrånare.', '2. Lyckas leverera budskapet.', '3. Lämnar brevet till fel kung. Orsakar krig.', '4. Kutar 42195 meter så fort han kan, meddelar    "Vi förlorade" och faller ned död.', '5. Säljer brevet till en filatelist för tre       banco. Frimärkssamlaren dansar lyckligt bort mot  solnegången.', '6. Ser välskapt ungmö på en balkong. Skriver en   serenad på baksidan av kuvertet.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (123, '123', 'Torkel leter efter stormhatt.', '1. Ramlar på en sibirisk jättejakttussilago.', '2. Hittar en stormhatt. Skaffar matchande kläder', '3. Hittar en stormhatt. Smakar på den.', '4. Hittar en stormhatt. Kommer ihåg vad han       skulle göra med den.', '5. Frågar botaniker till råds.', '6. Frågar blomsterförsäjeska till råds.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (124, '124', 'Torkel som skatteindrivare.', '1. Hanterar butig skattesmitare.', '2. En kollega påpekar att den indrivna skatten    skall lämnas till kungen.', '3. Driver in 2d6 gp med brutala metoder.', '4. Expedierar betalningsvägrande tiggarfamilj.', '5. Får betalning in natura.', '6. Får betalning in natura.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (125, '125', 'Torkel letar efter prinsessan Krusidull i Riddar  Rakskäggs slott.', '1. Hittar prinsessan, men Riddar Rakskägg vill    ha ett ord med i leken.', '2. Letar igenom hela slottet. Hittar ingen        prinsessa: "PROGNTUDJUUU!"', '3. Hittar lår. Inte prinsessans.', '4. Hittar ett övergivet smörgåsbord.', '5. Plundrar en av riddarens skattkammare och en   McApär', '6. Finner prinsessan Krusidull i ett torn.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (126, '126', 'Torkel skall befria prinsessan Krusidull ur       Riddar Rakskäggs slott.', '1. Hoppar på riddarens 1d6 vakter.', '2. Fest på slottet. Torkel glömer totalt bort     sitt uppdrag.', '3. Försöker gräva sig in.', '4. Tillverkar en bakdörr.', '5. Går till fel slott.', '6. Klättrar uppför ett rossaprytt och väl-        schamponerat gult, flätat rep. Hittar prinsessan  i andra änden.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (127, '127', 'Torkel har lyckats med sitt uppdrag och går till  kungen för att belönas.', '1. Torkel får inte den belöning han hoppats på.', '2. Torkel får en fin belöning.', '3. Torkel får prinsessan och halva kungariket.', '4. 3d6 flickor dyker upp och vill ha sina under-  hållspengar. Torkel förlorar hela belöningen och  dessutom 1d6 gp till.', '5. Utnämns till "Årets hjälte".', '6. Får medalj ur konungens hand.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (128, '128', 'Torkel möter häxa.', '1. Torkel håller sitt Uppdrag heligt.', '2. Häxan viftar till med en pinne.', '3. Torkel hittar en bubblande dryck.', '4. Torkel besöker Blåkulla.', '5. Torkel knycker hennes kvast.', '6. Torkel blir förhäxad.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (129, '129', 'Torkel kommer i kontakt med galler.', '1. Skakar galler.', '2. Dricker trolldryck.', '3. Äter helstekt vildsvin.', '4. Sänker piratskepp.', '5. Tycker Obelix är tjock.', '6. Konfererar med Miraculix.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (130, '130', 'Torkel imiterar fontän.', '1. Ringer till Rolf i den stora vita herr-        telefonen.', '2. Kör Gustavsbergsbussen.', '3. Lagar storaste pizzan i stada.', '4. Får upp något som han tidigare svalt.', '5. "Har jag ätit, eller skall jag äta?"', '6. "The spy who loved me."');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (131, '131', 'Torkel besöker drake med en skatt på 1d6*1000 gp', '1. Draken vaknar.', '2. Draken låtsades bara sova.', '3. Lyckas ta alla pengarna och smiter.', '4. Hackar ihjäl draken. Rusar därifrån i ett      lyckorus, men glömmer kvar alla pengarna och      lyckas inte hitta tillbaka igen.', '5. Lyckas ta med sig 1d6*100 gp. Får argsint      drake efter sig.', '6. Draken förvandlar sig till en eldig flicka.    Efter ett varmt mottagande ger hon Torkel en      mycket het kyss.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (132, '132', 'Torkel går i gruvgångar.', '1. Rånar 1d6+1 dvärgar.', '2. Sparkar till en stötta. Gruvan rasar! Torkel   rusar oskadd därifrån.', '3. Hittar reklamblad från LKAB.', '4. Åker malmvagn 42 mil nedför vindlande tunnlar  kommer ut genom ett hål i bergväggen och faller   300 meter ned i mjuka palmer.', '5. Gräver fram en juvel.', '6. Träffar snövit.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (133, '133', 'Torkel inför rättvisan.', '1. Diskuterar försvar med brännvinsadvokat.', '2. Blir dömd till döden.', '3. Studerar lagboken.', '4. Gör klart för domaren vad han ämnar göra om    han inte blir frikänd. Torkel blir frikänd.', '5. Alla utom Torkel somnar under försvarets       slutplädering.', '6. Gå i fängelset utan att passera GT!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (134, '134', 'Torkel letar efter hörn i ett runt rum.', '1. Blir iakttagen. 1d6 personer försöker hjälpa   honom.', '2. Springer, springer, springer...', '3. Hittar inget hörn.', '4. Ger upp efter lång och seg kamp.', '5. Blir snurrig i huvudet och drullar ut genom    fönstret.', '6. Hittar hörnet!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (135, '135', 'Torkel befinner sig uppe i ett fyrkantigt         slottstorn.', '1. Uppväcker 2d6 fladdermöss ur deras djupa       dvala. Blir irriterad då de flyger honom i        ansiktet.', '2. Undersöker flagstången.', '3. Träffar blixtrande trollkarl.', '4. Beundrar utsikten.', '5. Tröttnar på att sitta och glo ut igenom ett    fönster.', '6. "Befriar" inlåst prinsessa.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (136, '136', 'Torkel befinner sig upp i ett runt slottstorn.', '1. Möter 3d6 fladdermöss som är på väg ut. Får    en smart idé.', '2. "Oj, en sån massa makalösa manicker och        mojänger och gnistrande och blänkande prylar.     Detta måste undersökas noga!"', '3. Hittar slottets kassaskrin med 2d6*1000 gp,    och beslutar sig för att förvalta dem. Någon kan  ju komma och stjäla pengarna!', '4. Hör någon som klivande uppför trappan. Torkel  beslutar sig för att gömma sig i ett hörn.', '5. Beundrar utsikten.', '6. Finner flicka som somna med en slända i        famnen. Puss...');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (137, '137', 'Torkel möblerar om i vinkällare.', '1. Slottets ägare (konungen) hittar Torkel.', '2. Torkel dricker Österrikiskt lantvin. Tycker    det smakar kylarvatten.', '3. Torkel hittar bara alkoholfria viner. Blir     irriterad.', '4. Torkel ser konstiga skära djur med två         svansar. "Måschte vara tveschärtar..."', '5. Torkel tar med sig 1d6 vinflaskor som          färdkost.', '6. Kammarpiga kommer ned för att fylla på en      karaff.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (138, '138', 'Torkel träffar prinsessan.', '1. Torkel blir närgången. 1d6 friare anser sig    nödgade att gripa in.', '2. Torkel får inbjudan till den stora balen       imorgon.', '3. Prinsen, prinsessans trolovade kommer, så      Torkel tvingas gömma sig i ett lönnrum.', '4. Torkel blir bjuden på te.', '5. Puss, puss...', '6. Torkel känner ett visst intresse för           prinsessan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (139, '139', 'Torkel träffar konungen.', '1. Torkel fäller ett förolämande yttrande om      regentens heder.', '2. Torkel ges ett Uppdrag.', '3. Kungen tycker inte om Torkel. Torkel           deporteras till straffkoloni.', '4. Torkel ådöms att betala inkomstskatt:          1d6*100 gp.', '5. Torkel adlas.', '6. Torkel flirtar in sig hos drottningen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (140, '140', 'Torkel kommer till ett slott.', '1. Försöker övertyga 1d6 vakter om att han skall  in.', '2. Torkel lyckas göra ett stiligt stavhopp i      vallgraven.', '3. Torkel blir insläppt.', '4. Torkel släpps inte in.', '5. Torkel får audiens hos konungen.', '6. Torkel får audiens hos prinsessan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (141, '141', 'Torkel på bal.', '1. Bjuder upp en gift dam och retar hennes man.', '2. Dricker bål.', '3. Underhåller gästerna med solosång.', '4. Trampar prinsessan på tårna.', '5. Provar glassko. Får nageltrång.', '6. Får sista dansen med Askungen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (142, '142', 'Torkel dricker bål.', '1. Spiller ut sitt glas över närbelägen person.', '2. Spiller ut hela bålen.', '3. Försöker fånga fruktbitar utan att använda     händerna.', '4. Dansar på bordet.', '5. Drickar mera...', '6. Tar ett svalkande dopp i vallgraven.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (143, '143', 'Torkel möter vallgravsmonster.', '1. Slåss med sjöodjur.', '2. Räddar sig in slottet.', '3. Blir ett mumsigt mellanmål.', '4. Sätter ett nytt distriktrekord i hundra meter  vallgrav.', '5. Företar en strategisk reträtt.', '6. Försöker hålla sig flytande.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (144, '144', 'Torkel befinner sig i en mörk, dyster slotts-     källare.', '1. Möter 2d6 råttor.', '2. Hittar hemlig lönngång.', '3. Finner ingång till vinkällare.', '4. Möter en genomskinlig figur.', '5. Tycker källaren verkar tråkig. Går upp igen.', '6. "Undrar vart den här långa trappan leder? Och  varifrån kommer doften av svavel? Kan det vara    en tändsticksfabrik?"');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (145, '145', 'Torkel tänker gå upp i ett av slottets torn.', '1. Möter kungen i den smala trappan. Ingen vill   backa.', '2. Hittar lönngång i tornrummet.', '3. Går upp i ett runt torn.', '4. Går upp i ett fyrkantigt torn.', '5. Stannar ett tag för att beundra utsikten från  ett fönster i tornet.', '6. Möter fager ungmö i trappan. Ingen vill backa');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (146, '146', 'Torkel hemsöker slottets vindsvåning.', '1. Ser hemska person i spegeln. Dödar denna med   ett välrikta hugg.', '2. Hittar järnbeslagen gammal kista.', '3. Hittar något under det djupa dammtäcke i ett   mörkt hörn.', '4. Halkar på en gammal sill och faller nedför     trappan.', '5. Får ösnuva av allt damm. Nyser så att det r.   Störtar halvblind nedför trappan.', '6. Träffar söt kammarsnärta.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (147, '147', 'Torkel möter slottsspöke.', '1. Mäter sina krafter med det övernaturliga.', '2. Torkel tror inte på spöket.', '3. Torkel tar spökets lakan. Går på toga-party.', '4. Lilla spöket Laban...', '5. Torkel hittar målad blodfläck. Tar bort den    med Pinkertons Fläckurtagningsmedel.', '6. Det är Vita Frun...');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (148, '148', 'Torkel tar en simtur i vallgraven.', '1. Möter vallgravsmonster.', '2. Badning förbjuden.', '3. Klättrar upp i torn.', '4. Torkel får lunginflammation.', '5. Var är tvålen, broder?', '6. Räddar (i)fallen dam.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (149, '149', 'Torkel befinner sig inne i slottet.', '1. Möter något rödrosig rikstadsman.', '2. Trappa upp.', '3. Trappa ner.', '4. Spiraltrappa upp.', '5. Dörr ut.', '6. Torkel stöter oförhappandes på den unga        prinsessan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (150, '150', 'Torkel besöker bondgård.', '1. Slåss mot folkilsken tjur.', '2. Omskolas till jordbrukare.', '3. Dricker hemmagjord potatissaft.', '4. Hittar lucka i svinstian.', '5. Klättrar upp för stege.', '6. Träffar pigg piga.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (151, '151', 'Torkel på loftet.', '1. Slåss med 2d6 råttor.', '2. River ladan under försök att hitta ut.', '3. Träffar tomte.', '4. Torkel får tomtar på loftet.', '5. Får hösnuva.', '6. Trvligt i höstacken.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (152, '152', 'Torkel på promenad i civiliserade nejder.', '1. Följer trevlig landsväg.', '2. Hittar knuttimrad bondgård.', '3. Går in till staden.', '4. Hittar en bro.', '5. Besöker slott.', '6. Undersöker gammal övervuxen slottsruin.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (153, '153', 'Torkel i kloaksystem.', '1. Möter 2d6 råttor.', '2. Stöter ihop med en alligator.', '3. Utforkskar mystisk sidogång.', '4. Hittar lucka upp.', '5. Följer stömmen nedströms.', '6. Träffar smutsig flicka.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (154, '154', 'Torkel uppför sig naturligt.', '1. Torkel utför naturliga rörelser.', '2. Torkel släcker sin naturliga törst.', '3. Torkel blir naturlik.', '4. Torkel utträttar sina naturliga behov i        därför avsett utrymme.', '5. Torkel blir naturligt vis. Försöker finna det  yttersta svaret.', '6. Torkel kommer överens med en flicka om att     uträtta deras ömsesidiga naturliga drifter.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (155, '155', 'Torkel försöker befria svärd från sten.', '1. Diskuterar köordning med 1d6 svärdshungrande   kungaaspiranter.', '2. Torkel drar - sväret sitter kvar.', '3. Torkel drar - svärdet brister!', '4. Torkel drar - gör en graciöst en baklänges-    saltomotal.', '5. Torkel drar.', '6. Torkel välter stenen, och hittar en gång       under... Det är den världsunderspännande          dungeonen!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (156, '156', 'Torkel gör närmanden mot haremsdam.', '1. Hon tillkallar 1d6 butiga eunucker.', '2. Oljig shejk med 1d6 soldater stormar in.', '3. Badar mysigt varmbad med henne.', '4. Hon känner sig inte trakterad av barbariska    parningsriter.', '5. Hon har gått i en bra skola.', '6. Hon visar Torkel några enkla små tricks.       Torkel helt salig.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (157, '157', 'Torkel i slottsruin.', '1. Träffar på 2d6 råttor.', '2. Torkel lyckas hitta den gamla vallgraven.      (PLASK!!!)', '3. Trillar ner i ett hål.', '4. Hittar 1d6 stenar.', '5. Träffar källarmästaren.', '6. Hittar mojäng i en stenhög.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (158, '158', 'Torkel åker båt.', '1. Det kommer 1d6 sjörövare och hälsar på.', '2. Torkel blir sjösjuk.', '3. Torkel drar ur bottenpluggen för att släppa    ut vattnet ur båten.', '4. Torkel övar svärdshug på masten. Båten börjar  sjunka.', '5. Medresenärerna tröttnar på Torkel, och         släpper av honom vid första bästa strand.', '6. Torkel upptäcker att fartyget har en vacker    galjonsfigur.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (159, '159', 'Torkel på flotte.', '1. Ett skepp kommer lastat med 2d6 sjörövare.', '2. Träffar vinddriven grek.', '3. Druttar i havet.', '4. Flyter iland vid en vacker badstrand.', '5. Gör strandhugg på öde ö.', '6. Träffar plaskande sjöjungfru.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (160, '160', 'Torkel fryser.', '1. Går på jakt.', '2. Träffar jultomten.', '3. Hittar Nordpålen.', '4. Tas av polisen.', '5. Spricka i pol-isen. Torkel lider in.', '6. Gnuggar näsa med söt urinnevånare.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (161, '161', 'Torkel i uråldrig, förlorad djungeldal.', '1. Kämpar mot Tyrannosarus Rex.', '2. Får lift av man med leopardmönstrade           kalsonger.', '3. Blir tillfångatagen av en stam uråldriga,      förlorade vildar. Torkel beslutar sig för att     stanna hos sina intrel... intel... intlelktuella  jämlikar.', '4. Hittar latjo lite pryl.', '5. Badar i het källa.', '6. Vilar vid denna källa. Träffar Ulla.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (162, '162', 'Torkel hos Jultomten.', '1. Slåss mot illvättar.', '2. Torkel får en present.', '3. Torkel får renskav.', '4. Torkel får skoskav.', '5. Torkel bjuds på svindlande slädtur.', '6. Träffar söt tomtenissa.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (163, '163', 'Torkel på hemmet för mycket mycket nervösa personer.', '1. Stryper översköterskan.', '2. Spelar basket med lång idian.', '3. Psykiater råder Torkel att vara naturlig.', '4. Åker på båttur med likasinnade.', '5. Kastar vask genom fönster.', '6. Torkel blir lobotomerad.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (164, '164', 'Torkel i igloo.', '1. Slåss mot 1d6 hundar.', '2. Torkel tror igloo är en glass.', '3. Hittar öppning i golvet.', '4. Tar ett stort isförråd till sina drinkar.', '5. Fryser. Tänder eld. Igloon smälter.', '6. Fryser. Värmer sig med eskimö.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (165, '165', 'Torkel jagar.', '1. Går i närkamp med isbjörn.', '2. Går vilse. Allting är vitt, vitt, vitt...', '3. Hittar igloo.', '4. Hittar bortglömd dal.', '5. Ramlar ner i ravin, försed med trevlig grotta', '6. Träffar Sirbirisk jättejakttussilago.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (166, '166', 'Torkla har njutånger.', '1. Slåss med stork.', '2. Äter konstig växt - naturligt missfall.', '3. Dör i barnsäng.', '4. Får ett barn.', '5. Torkla får 1d6 snorungar.', '6. Torkla får ett präktigt gossebarn. Tränar upp  detta till en äkta butig barbar.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (167, '167', 'Torkel på Paladinskolan.', '1. Diskuterar alignment med 1d6 paladiner.', '2. Blir nästan religiös.', '3. Går en kurs i den ädla konsten att uppvakta    en dam.', '4. Alla har plötsligt åkt på korståg - och bara   präster kvar.', '5. Snokar rätt på rustningskammaren.', '6. Förtjusande paladinna.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (168, '168', 'Torkel på öde ö.', '1. Bråkar med 1d6 vildar.', '2. Bjuder öns kannibaler på närande måltid.', '3. Bygger sandslott.', '4. Bygger flotte.', '5. Gömmer sig i en kokosnöt, får lift med en      svala.', '6. Upptäcker att fredag är en flicka.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (169, '169', 'Torkel hemma hos norna.', '1. Snubblar rakt in i hennes väv och råkar ha av  1d6*10 trådar.', '2. Blir visad vägen till Valhall.', '3. Hugger ved.', '4. Nornan ser på Torkel, och inser sitt misstag.', '5. Blir spådd.', '6. Mutar till sig ett längre liv.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (170, '170', 'Torkel i syndens näste.', '1. Muckar gräl med dörrslusken, en ogre från      ABAB.', '2. Besöker föreståndarinnan, kostnad 1d6 gp.', '3. Besöker brunögd blondin, kostnad 3d6 gp.', '4. Besöker svarthårig skönhet, kostnad 6d6.', '5. Besöker blåögd brunett, kostnad 2d6*10 gp', '6. Besöker rödhårig toppensnäcka, kostnad         3d6*10 gp.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (171, '171', 'Torkel med flicka från glädjehus.', '1. Gör flickan arg. Han ropar på hjälp och 1d6    äventyrare dyker upp för att skydda henne.', '2. Torkel smittas av mystisk sjukdom.', '3. Torkel blir smittad av immunbristsjukdom,      avlider senare av förkylning.', '4. "En gång till, det här var ju riktigt kul."', '5. Nöjd och belåten.', '6. "Det går ju fint det här!" Fortsätter av bara  farten.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (172, '172', 'Torkel möter slottsruinens rättmätige ägare.', '1. En wraith.', '2. En vampyr.', '3. Ett troll.', '4. En lich.', '5. Ett spöke.', '6. En varulv.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (173, '173', 'Torkel hittar juvel.', '1. Det var visst en demon inspärrad i juvelen.', '2. Värdelös glasbit.', '3. Bärnsten.', '4. Pärla.', '5. Diamant.', '6. Flicka med smaragdögon, rubinrött hår och      gnistrande leende.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (174, '174', 'Torkel penetrerar pyramid.', '1. Möter ett gäng gravskändare.', '2. Torkel vänder om och går tillbaka.', '3. Fyrvägskorsning.', '4. Intressant gång in till vänster.', '5. "Typiskt ställe att gömma en hemlig dörr på!   Att de aldrig lär sig!"', '6. Det var en gång in till höger, som är upplyst  av nästan utbrända facklor.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (175, '175', 'Torkla i harem.', '1. Träffar eunuck. Besviken.', '2. BLir shejkens favorit. Stannar kvar.', '3. Uppviglar haremsdamerna mot shejken.', '4. För få karlar.', '5. Badar turkisk bastu.', '6. Träffar shejk Yerbouti.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (176, '176', 'Torkel finner ett fyrvägsskäl i pyramiden.', '1. Det kommer 1d6 fladdermöss nervöst flaxande.   Torkel anser att de är i vägen.', '2. Torkel lommar rakt fram, eftersom någon lagt   en gp där.', '3. Torkel drullar in i den vänstra gången         tänkande: "På vänster hand sitter tummmen till    höger, eller var det: på höger hand sitter        tummen till vänster?"', '4. Hör ljud från den högra gången. "Tänk om det   är någon flicka med mycket pengar!"', '5. Vänder sig om och går tillbaka.', '6. Hittar borttappad juvel, Torkel beslutar       enhälligt att detta föremål skall tas hand av     lämplig myndighetsperson (Torkel) så juvelen      inte kommer i orätta händer.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (177, '177', 'Torkel i smal pyramidgång.', '1. Möter kringströvande mumie.', '2. Kommer fram till ett rum.', '3. Mystisk gång går in till vänster. Den måste    undersökas!', '4. Börjar känna igen sig.', '5. "Konstiga sprickor i väggen..."', '6. Kommer fram till en korsning.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (178, '178', 'Torkel letar efter hemliga dörrar.', '1. Vaktmonster dyker upp från ingenstans.', '2. Letar frenetiskt. När Torkel inte hittar       något, riktar han en våldsam spark mot den        förmodade dörren.', '3. "Äh fan, det finns ingen dörr här..."', '4. En stor panel glider åt sidan.', '5. Finner spak. När han drar i den, öppnas en     lucka i golvet - precis under Torkel.', '6. En stege trillar ner i huvudet på Torkel.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (179, '179', 'Torkel i bred gång inuti pyramid.', '1. 2d6 zombier kommer glatt skuttande mot Torkel', '2. Torkel beundrar de många målningarna på        väggarna. Tycker det är synd att serierna saknar  pratbubblor.', '3. Torkel får syn på en öppning innen i en        dåligt upplyst nisch.', '4. Torkel ser borttappad moj på golvet.', '5. Torkel närmar sig försiktig en korsning.', '6. Smiter in i en sidogång.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (180, '180', 'Torkel virrar omkring i villande gångar.', '1. Träffar 1d6 likaså vilsekomna skelett.', '2. "Var jag inte här igår?"', '3. "Vart går den här gången?"', '4. Efter ett tag stöter Torkel ihop med en liten  vit hund, en kort snubbe med gula mustascher och  en stor rund klund som troligen föll ner i        grytan som liten. Tack vare hundens spårsinne     kommer de ut, och den belönas med 1d6 ben.', '5. "Hm... Här ser det ut som om det skulle        finnas en hemlig dörr... det vore väl tur!"', '6. Hittar ihjälsvulten äventyrare med 2d6 gp och  ett vapen i handen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (181, '181', 'Torkel i ett pyramidrum.', '1. Torkel får en bandagerad arm om sin strupe då  han träder in.', '2. Samlar antika konstföremål.', '3. Plockar upp en konstig formad käpp.', '4. Hittar en inlindad ma i en kista. "Stackare,   han måste ha slagits sig rejält!"', '5. Traskar ut genom en öppning på andra sidan     rummet.', '6. Torkel hittar en lucka i mosaikgolvet.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (182, '182', 'Torkel har funnit ett hemligt rum i pyramiden.', '1. 1d6 mumier bor här. De tycker inte om att en   stor, klumpig barbar kommer indrullande.', '2. Mitt i rummet står en stor kista i sten.       Torkel tror att den måste innehålla mycket        skatter.', '3. Det har redan varit gravplundrare här. Av      rummets utformning att döma måste det en gång i   tiden innehållit fabulösa skatter. "Fy farao!!!"', '4. "Vad är det som ligger i det där mörka         hörnet?"', '5. Torkel hittar en hög med glittrande guldmynt   samt en juvel.', '6. Dörren av massiv sten slår igen bakom Torkel.  Det dröjer ett bra tag innan den går upp igen...');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (183, '183', 'Torkel studerar en mumie i dess naturliga miljö.', '1. Mumien tycker inte om Torkels uppförande.', '2. Torkel tycker inte om lukten.', '3. Torkel lindar upp bandagen.', '4. Torkel hittar en flaska balsameringsvätska.', '5. Torkel hittar 2d6 gp i sarkofagen.', '6. Torkel snubblar och faller ner i sarkofagen.   Locket slår igen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (184, '184', '"Vad är frågan?"', '1. "Att vara eller inte vara..."', '2. "Att vara eller inte vara..."', '3. "Att vara eller Kirunavaara."', '4. "Att hacka eller inte hacka."', '5. "IIB or not IIB... <glugg, glugg, glugg>"', '6. "Att älska eller inte älska..."');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (185, '185', 'Torkel ligger i en sarkofag.', '1. En råtta!', '2. Mörkt och kvalmigt.', '3. Efter en lång och hård kamp lyckas Torkel få   upp locket på sarkofagen. Lättad kryper han ut.', '4. Torkel försöker lyfta av det tunga sten-       blocket, men klarar det inte.', '5. Torkel hittar av en slump en lönngång i        botten av sarkofagen.', '6. Torkel märker att han är instängd med en       kvinnlig mumie.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (186, '186', 'Torkel blir uppsprättad.', '1. Torkel tycker inte om att bli behandlad som    en kalkon. Diskuterar igenom saken med kirurgen.  Avdelningschefen beslutar sig för att ge honom    en remiss...', '2. Torkel känner sig uppriven.', '3. Torkel får smaklökarna bortopererade.', '4. Donator behövs till hjärttransplantation.      Torkels hjärndödhet konstateras.', '5. Kastreras.', '6. Torkel blir utsatt för en könsbytesoperation.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (187, '187', 'Torkel är smaklös.', '1. Äter surströmming med friterade jordgubbar     och litervis av remouladsås.', '2. Blandar öl och tomatpuré.', '3. Äter skriksvampsstuvning.', '4. Klär sig i pistaschgröna bomullsbyxor med      stora skära blommor, chockrosa skjorta med lila   snedränder samt saffransgul väst och ljusblå      stövlar.', '5. Gör succé som konstkritiker.', '6. Uppvaktar zombieflicka.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (188, '188', 'Torkel är hjärtlös.', '1. Sparkar undan krykan för en krigsveteran.', '2. Stjäl mat från svältande tiggare.', '3. Säger "NI!!!" till gammal dam.', '4. Matar parkduvor med laxermedel.', '5. Får ett konstgjort hjärta (JARVIK 7)           inopererat.', '6. Torkel beter sig rent ut sagt fasansfullt      illa!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (189, '189', 'Torkel är vidrig.', '1. Byter ut ögondroppar mot modellplanslim.', '2. Förgiftar en blind mans ledarhund.', '3. Tvångsmatar diabetiker med polkagrisar.', '4. Byter ut dopvatten med svavelsyra vid ett      barndop.', '5. Gjuter in rakblad i rutschbana.', '6. kastar in ett getingbo i en dambastu.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (190, '190', 'Torkel i labratorium.', '1. Möter lång person med skruv genom halsen som   ser ut som Boris Karloff.', '2. Träffar Dr Frankenstein. Får hjärnan utbytt.', '3. Drumlar in i en alignmentdetektor. Visarna     slår i bottem på "Chaotic Stupid" varefter        maskinen exploderar!', '4. Drar i spak. Utlöser laboratoriets själv-      förstörelsemekanism.', '5. Rotar i verktygslådan.', '6. Leker med provrören.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (191, '191', 'Torkel letar vidare.', '1. Använder helig handgranat: "1...2...5..."', '2. Bygger trojansk träkanin för att inte ett      slott, men glömmer tyvärr att gå in i den. Får    den och 1d6 sorterade husdjur i skallen.', '3. Räddar en prins som håller på att bli bort-    gift.', '4. Ser en förbiflygande svala med kokosnöt.', '5. Möter riddarna som säger "NI!" Hugger ner det  största trädet i skogen med en sill, varefter     riddarna låter honom passera.', '6. Torkel lockas till ett slott av en graal-      formad lampa. Där bor tio tjog vardera blondiner  och brunetter mellan 18 och 24 år som påstår sig  aldrig ha sett en karl förut.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (192, '192', 'Torkel på rätt spår.', '1. Blir påhoppad av vild mördarkanin.', '2. Hittar ett integraltecken men kommer till      insikt: "Detta är inte graalen."', '3. Idkar frågesport vid hisnande avgrund.         Torkel vet att Stockholm är huvudstaden i         Assyrien.', '4. Blir tagen av kravallpolis.', '5. Hittar den Heliga Graal.', '6. Hittar en ring med lustig inskription.         Struntar i den Träliga Graalen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (193, '193', 'Torkel klättrar i skogens högsta träd.', '1. Trädet visar sig vara en ent. Den gillar inte  att bli trampad i ansiktet.', '2. Möter mistelkärande man vid namn Miraculix.', '3. Torkel ser något mycket intressant i en okänd  del av skogen. Han beslutar sig för att genast    undersöka detta.', '4. Halkar på en sill, trillar ner och skapar ett  barbarformat hål i marken.', '5. Hittar juvelbesatt guldägg från firma Carl     Fabergé i ett skatbo.', '6. Hittar en alvflicka i trädtoppen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (194, '194', 'Torkel utforskar okänd skog.', '1. Möter en stor varg med blå snickarbyxor och    stor ful hatt.', '2. Möter en liten varg med röda snickarbyxor och  stor ful hatt. Torkel blir visad på rätt spår.', '3. Träffar tre små grisar.', '4. I en liten glänta. upplyst från ovan av den    gyllengula solens klara strålar, står ett jätte-  likt gammalt mossbetäckt stenblock med ett svärd  nedstucket ända till det ornamenterade och        juvelbesatta hjaltet. Torkel drar...', '5. Finner en liten stuga med tre sängar och tre   fat gröt. En liten flicka med gyllene skor -      förlåt, hår - dyker upp, tätt följd av tre        björnar.', '6. Träffar välsvarvad rödluva.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (195, '195', 'Torkel är vilse i öknen.', '1. Torkel halkar ned i ett jättemyrlejons         fångstgrop.', '2. Torkel känner sig törstig.', '3. Torkel använder ett sandpapper som karta.      Hittar rätt.', '4. Torkel ser en hägring.', '5. Torkel snubblar på en liten triangelformad     sten. Beslutar sig för att gräva upp den. Gräver  länge.', '6. Hittar INGEN flicka!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (196, '196', 'Torkel i det äkta ståndet.', '1. Kommer på frun med älskare.', '2. Lyckligt gift.', '3. Tar gift.', '4. Vill bli lyckligt ogift.', '5. Svärmor uppmanar Torkel att ta jobb som        kontorist.', '6. Skaffar sig älskarinna.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (197, '197', 'Tärningen är kastad.', '1. En etta.', '2. En tvåa.', '3. En trea.', '4. En fyra.', '5. En femma.', '6. En sexa - extraslag!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (198, '198', 'Torkel kommer till luftigt utrymme.', '1. I rummet står en kista väl gömd i skuggorna.', '2. Gången mynnar ut i rökigt rum utan tak.', '3. Gradvis smalnar det av igen till en gång.', '4. Kommer till en bro över en underjordisk flod.', '5. Gången delar upp sig i många  slingrande små-  gångar.', '6. En grotta vars tak hålls upp av stora trä-     konstruktioner leder ut härifrån.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (199, '199', 'Torkel i labyrint.', '1. Möter Minotaurus.', '2. Torkel hittar en tamp.', '3. Torkel hittar ärter som någon strött ut i en   lång rad.', '4. Torkel hittar bara återvändsgränder.', '5. Följer forspår efter en stor, tungt lastad     man, som får sällskap av en till... och en        till... och en till... inser efter 1d6+12 varv    att han spårar sig själv.', '6. Leds ut av Ariadne.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (200, '200', 'Torkel blir pussad av houri.', '1. "Kiss of Death."', '2. "Kiss of Polymorph."', '3. "Kiss of Disease."', '4. "Kiss of Extacy."', '5. "Kiss of Change Sex."', '6. "Kiss of Love."');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (201, '201', 'Torkel i spelhåla.', '1. Torkel möter enarmad bandit.', '2. Torkel stoppar en gp i en lustig låda.', '3. Torkel spelar tärning.', '4. Torkel spelar utländskt spel.', '5. Torkel slår sig ned vid ett spelbord: "Hejsan  grabbar! Vad spelar ni här?"', '6. Torkel spelar kort.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (202, '202', 'Torkel spelar magiskt arkadspel.', '1. Space invaders.', '2. Breakout.', '3. Pac-Man.', '4. Frogger.', '5. Defender.', '6. Ms PacMan.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (203, '203', 'Torkel i Drakborgen.', '1. Torkel möter 1d6 orcher.', '2. Torkel möter en tanig vätte.', '3. Torkel fallar ned i ett bottenlöst hål.', '4. Torkel drabbas av "trollkarens förbannelse".', '5. Torkel hittar en intressant sak i en hög med   skräp.', '6. Torkel finner draken.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (204, '204', 'Torkel får kontakt med förbipasserande:', '1. Ett gäng med 1d6+4 ruffiga banditer, bärande   på något intressant.', '2. 1d6+2 äventyrare.', '3. Kungens sändebud läser upp en proklamation om  ett välbetalt uppdrag.', '4. Lättretad trollkar.', '5. Lokal själasörjare.', '6. En av Torkels gäldenärer. Torkel smiter in i   på en bakgata.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (205, '205', 'Torkel finner någonting intressant.', '1. "Vad är det här för mystiska sprickor i        golvet?"', '2. "Ett svärd? Här?"', '3. Något blänker i ett hörn.', '4. En pinne nån klottrat på.', '5. "Ah, färska fotspår!"', '6. Pikant klotter på väggen.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (206, '206', 'Torkel offrar.', '1. Offrar tid.', '2. Offar sig.', '3. Får gengåva.', '4. Gudarna är på skämt humör.', '5. Gudarna sänder Torkel på ett Uppdrag i hans    drömmar.', '6. Panisk offerrit.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (207, '207', 'Torkel möter urbiffigt monster.', '1. Balrog.', '2. Beholder.', '3. Drake.', '4. Traalsk dreglande dånfink.', '5. Lindorm.', '6. Nazgul.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (208, '208', 'Torkel på hjulångare.', '1. Anfalls av yxförsedd viking.', '2. Lodar.', '3. Spelar högt.', '4. Råkar täppa till säkerhetsventil vid ett       olämpligt tillfälle.', '5. Finner förbiflytande hus.', '6. Träffar Alice.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (209, '209', 'Torkel möter Tarzan.', '1. Lär sig slåss som en riktig djungelbjörn.', '2. Försöker lära sig liansvingandets konst.', '3. Torkel tröttnar på banandiet.', '4. Följer snärjig igenvuxen djungelstig.', '5. För lord Greystoke till civiliserade trakter.', '6. Tycker Jane är söt i leopardpälsbikini.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (210, '210', 'Torkel vid fördolt djungeltempel.', '1. Bränns på soltänt bål.', '2. Fortsätter på den mystiska djungelstigen.', '3. Står ivägen för skum rit.', '4. Finner whiskeyberusad ochförsedd, skäggig      sjökapten med kreativt språkbruk.', '5. Får sju kristallkulor i skallen.', '6. Torkel hittar då dåraktigt dåligt dold         inkaskatt.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (211, '211', 'Torkel med gyllende skatt.', '1. Tvistar om ägorätt med oxpisk- och hatt-       försedd äventyrare.', '2. Nitisk skatteindrivare dyker upp från          Ingenstans. Driver in alltihop, plus en extra     förmögenhetsskatt på 3d6*100 gp.', '3. Torkel skriver upp på en lapp var skatten      finns, och när han efter tag kommer på att han    inte kan skriva, har han glömt bort lokaliteten.', '4. Här finns lustiga prylar!', '5. Torkel släpar med sig 1d6*1000 gp och en       mystisk juvel.', '6. Spenderar allt, och ännu lite till på väldigt  vackra kvinnor.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (212, '212', 'Torkel under gudomlig inverkan.', '1. Tvingas slåss mot guden Chthuhlhuh (huh?).', '2. Torkel försvinner i rök och damm.', '3. Torkel känner ett lustigt begär efter flugor.', '4. Torkel känner sig vissen.', '5. Torkel känner sig vissen.', '6. Torkel hamnar i schlagerfestival.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (213, '213', 'Torkel som köttätande växt.', '1. Möter argsint fluga.', '2. Anfalls av hungrig kanin.', '3. Gräsbrand.', '4. Snäll druid passerar.', '5. Det regnar.', '6. Torkel möter bi. Får 1d6 busfrön.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (214, '214', 'Torkel i Olympen.', '1. Sår draktänder; slåss med 2d6 uppväxande       soldater.', '2. Hittar lucka i reglerna. Tar medalj i          olympiska spelen.', '3. Blir stjärnbild.', '4. Äter ambrosia och dricker nektar.', '5. Avlöser Atlas.', '6. Dricker afrodisaka med Afrodite.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (215, '215', 'Torkel i Hades.', '1. Får utskällning av Cerberus.', '2. Ser sig om efter Orfeus.', '3. Stjäl en färja.', '4. Torteras i Tartaros.', '5. Badar i Lethe. Glömmer allt.', '6. Sjunger för Euredike.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (216, '216', 'Torkel och Odysseus.', '1. Slåss mot cyklop.', '2. Förliser mellan Skylla och Charybdis.', '3. Besöker Lotofagernas ö.', '4. Hör sirensång. Sjunger med.', '5. Bygger trähäst.', '6. Driver i land hos Nausikaa.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (217, '217', 'Torkel på korståg.', '1. Omvänder 1d6 otrogna hundar.', '2. Invaderar oljerikt grannland.', '3. Hittar Dödahavsrullarna.', '4. Blir krigshjälte.', '5. Känner sig bombad.Scuddar stoftet av sina kor.', '6. Befriar ett kuvat land från en före detta      helig relik.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (218, 'Femtioelva', 'Torkel hackar kobolder.', '1. Dräper en kobold.', '2. Dräper två kobolder.', '3. Dräper tre kobolder.', '4. Dräper fyra kobolder.', '5. Dräper fem kobolder.', '6. Bara en stackars förskrämd koboldflicka kvar!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (219, '666', 'Torkel i helvetet.', '1. Grälar med Hornper.', '2. Skyfflar kol.', '3. Hittar en mycket hemlig trappa.', '4. Torkel svettig.', '5. Torkel behöver en extra tröja. (-273,15°C)', '6. Träffar succubus.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (220, '714', 'Torkel flyger till Sidney.', '1. Träffar det ondas genius.', '2. Träffar flygande tefat.', '3. Trycker på en statys ögon.', '4. Landar.', '5. Drabbad av minnesförlust.', '6. Träffar miss Australien.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (221, '1000', 'Torkel i Milliways.', '1. Möter hungrig monster.', '2. Blir bjuden på en "Pan Galactic Gargle         Blaster". Tycker att hans huvud har slagits in    av en citronskiveomsluten guldtacka.', '3. Betraktar Universums förintelse. Festen är     slut och Torkel får lift med ett rymdskepp.', '4. Profeten Zarquon kommer.', '5. Parkerar 1d6 bilar.', '6. Träffar Eccentrica Gallumbits.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (222, 'NCC 1701', 'Torkel ombord på USS Enterprise.', '1. Pillar på knapp - spränger 1d6 klingoner i     bitar - frisläppt.', '2. Pillar på knapp - spränger USS Enterprise i    småbitar.', '3. Hittar telefon.', '4. Hittar nedstrålningsrum.', '5. Hittar luftsluss - blir mirakulöst räddad.', '6. Träffar löjtnant Uhura.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (223, '1917', 'Torkel spelar rysk roulette.', '1. Klick!', '2. Klick!', '3. Klick!', '4. Klick!', '5. Klick!', '6. Pang!');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (224, '4077', 'Mobilt fältsjukhus dyker upp för att hjälpa Torkel.', '1. Går rond med doktorn.', '2. Doktorn går ronden.', '3. Torkel opereras av skelögd slaktare.', '4. "Ta en magnecyl och ring oss i morgon!"', '5. Förvirrar sig ner i kulverten.', '6. Vårdas av blont välpumpat bombnedslag.');
INSERT INTO table_text (table_id, label, title, option_1, option_2, option_3, option_4, option_5, option_6) VALUES (225, '01994639', 'Torkel slår slumpmässigt telefonnummer,           diskuterar med telefonsvarare.', '1. Förstör telefonen.', '2. Blir frälst.', '3. Blir rasist.', '4. Skrattar ihjäl sig.', '5. Får nippran.', '6. Får växeltelefonistens namn och adress.');
